const Sidebar = ({ openSidebar, setOpenSidebar }) => {
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showRegisterModal, setShowRegisterModal] = useState(false);

  return (
    <>
      <aside
        className={classNames("landing-sidebar", {
          "landing-sidebar--opened": openSidebar,
        })}
      >
        <div className="landing-sidebar__close-btn">
          <svg
            className="close"
            onClick={() => setOpenSidebar(false)}
            width="14"
            height="14"
            viewBox="0 0 14 14"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M13 1L1 13M1 1L13 13"
              stroke="#AE7AFF"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        </div>

        <ul className="landing-sidebar__menu">
          {menu.map((item) => {
            return (
              <Link
                key={item.id}
                to={item.link}
                onClick={() => setOpenSidebar(false)}
              >
                <li>
                  {item.image}
                  {item.name}
                </li>
              </Link>
            );
          })}
        </ul>

        <div className="buttons">
          <button onClick={() => setShowLoginModal(true)}>Log in</button>
          <button onClick={() => setShowRegisterModal(true)}>Sign up</button>
        </div>

        <ul className="landing-sidebar__links">
          {links.map((link) => {
            return <li key={link.id}>{link.image}</li>;
          })}
        </ul>
      </aside>

      <LoginModal
        modalShow={showLoginModal}
        setModalShow={() => setShowLoginModal(false)}
      />
      <RegisterModal
        modalShow={showRegisterModal}
        setModalShow={() => setShowRegisterModal(false)}
      />
    </>
  );
};

export default Sidebar;
